package javasessions;

public class LoopsConcept {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for(char ch = 'a'; ch<='z'; ch++) {
			
			System.out.println(ch);
		}
		
		for(char ch = 'a'; ch<='z'; ch++) {
			
			System.out.println((byte)ch);
		}
		
		

	}

}
